import { useReducer } from "react";

const counterReducer = (state = { count: 0 }, action) => {
    if (action.type === 'increment') {
        return {
            count: state.count + 1
        };
    }

    if (action.type === 'decrement') {
        return {
            count: state.count - 1
        };
    }

    return state;
  }


const ActionAwareApp = () => {
    const [state, dispatch] = useReducer(counterReducer);

    const dispatchIncrement = () => dispatch({ type: 'increment '});
    const dispatchDecrement = () => dispatch({ type: 'decrement '});

    return (
        <div>
            <div>{state.count}</div>
            <button onClick={dispatchIncrement}>Increment</button>
            <button onClick={dispatchDecrement}>Decrement</button>
        </div>
    )
}

export default ActionAwareApp;
