const StateAwareApp = () => {
    const [counter, setCounter] = useState(0);

    return (
        <div>
            <div>{counter}</div>
            <button onClick={() => setCounter(prev => prev + 1)}>Increment</button>
            <button onClick={() => setCounter(prev => prev - 1)}>Decrement</button>
        </div>
    )
}

export default StateAwareApp;
