import { useReducer } from "react";
import { capitaliseRandomLetter, deleteEverySecondLetter } from "./helpers";

const textReducer = (state, action) => {
    if (action.type === 'capitalise') {
        return {
            text: capitaliseRandomLetter(state.text),
        };
    }

    if (action.type === 'delete') {
        return {
            text: deleteEverySecondLetter(state.text),
        };
    }

    return state;
  }


const ActionAwareApp = () => {
    const [state, dispatch] = useReducer(textReducer, { text: 'lorem ipsum dolor sit amet' });

    const dispatchIncrement = () => dispatch({ type: 'increment' });
    const dispatchDecrement = () => dispatch({ type: 'decrement' });

    return (
        <div>
            <div>{state.count}</div>
            <button onClick={dispatchIncrement}>Increment</button>
            <button onClick={dispatchDecrement}>Decrement</button>
        </div>
    )
}

export default ActionAwareApp;
