export const capitaliseRandomLetter = (str) => {
    const randomIndex = Math.floor(Math.random() * str.length);

    return str.substring(0, randomIndex) +
        str[randomIndex].toUpperCase() +
        str.substring(randomIndex + 1);
}

export const lowercaseLetterAtIndex = (str, index) => {
    return str.substring(0, index) +
        str[index].toLowerCase() +
        str.substring(index + 1);
}

export const deleteEverySecondLetter = (str) => {
    return str
        .split('')
        .filter((_, index) => index % 2 === 0)
        .join('');
}

export const restoreDeletedLetters = (str, deletedLetters) => {
    return str
        .split('')
        .filter((_, index) => index % 2 === 0)
        .join('');
}
